import React from 'react';
import styled from 'styled-components';

const SkillsContainer = styled.div``;

const SectionTitle = styled.h2`
  font-size: 2rem;
  color: #2c3e50;
  margin-bottom: 1.5rem;
  border-bottom: 2px solid #eee;
  padding-bottom: 0.5rem;
`;

const SkillsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const SkillCategory = styled.div``;

const CategoryTitle = styled.h3`
  color: #34495e;
  margin-bottom: 1rem;
`;

const SkillList = styled.ul`
  list-style: none;
  padding: 0;
`;

const SkillItem = styled.li`
  margin-bottom: 0.5rem;
  color: #666;
  display: flex;
  align-items: center;
  
  &:before {
    content: "•";
    color: #3498db;
    margin-right: 0.5rem;
  }
`;

function Skills() {
  return (
    <SkillsContainer>
      <SectionTitle>Skills</SectionTitle>
      <SkillsGrid>
        <SkillCategory>
          <CategoryTitle>Technical Skills</CategoryTitle>
          <SkillList>
            <SkillItem>JavaScript (ES6+)</SkillItem>
            <SkillItem>React.js</SkillItem>
            <SkillItem>HTML5 & CSS3</SkillItem>
            <SkillItem>Node.js</SkillItem>
            <SkillItem>Git & Version Control</SkillItem>
            <SkillItem>Responsive Web Design</SkillItem>
          </SkillList>
        </SkillCategory>
        
        <SkillCategory>
          <CategoryTitle>Soft Skills</CategoryTitle>
          <SkillList>
            <SkillItem>Problem Solving</SkillItem>
            <SkillItem>Team Collaboration</SkillItem>
            <SkillItem>Communication</SkillItem>
            <SkillItem>Time Management</SkillItem>
            <SkillItem>Adaptability</SkillItem>
          </SkillList>
        </SkillCategory>
      </SkillsGrid>
    </SkillsContainer>
  );
}

export default Skills;
